import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PlaylistContext from '../PlaylistContext'
import './Homepage.css'
import GenreCheck from './GenreCheck'


export default class HomePage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            selected: [],
            time: '1:00'
        }
    }

    clickStore = (e, status) => {
        if (!status)
            this.setState({ selected: [...this.state.selected, e.target.value] })
        else {
            let temp = this.state.selected.filter(item => {
                if (item !== e.target.value)
                    return item
            })
            this.setState({ selected: temp })
        }

    }

    minChange = (e) => {
        if (e.target.value.length == 1)
            e.target.value = '0' + e.target.value;

    }
    // prepended_number = String(number).padStart(2, '0')

    render() {
        return (
            <PlaylistContext.Consumer>
                {(value) => {
                    let genres = value.genres.map(genre =>
                        <GenreCheck id={genre.id} name={genre.name} key={genre.id} clickE={this.clickStore} />
                    )

                    return genres && (
                        <div id="play-stage">
                            <label htmlFor="name-input">Playlist Name: </label>
                            <input type="text" id="name-input" />
                            <label htmlFor="hour-length">Playlist length:</label>
                            <input type="number" id="hour-length" min="00" max="3" placeholder="hour" />:<input type="number" id="minute-length" min="0" max="59" defaultValue="00" onChange={this.minChange} placeholder="min" />
                            <div id="genres">
                                {genres}
                            </div>
                            <footer><Link to={'/'}>Existing Playlists</Link></footer>
                        </div>
                    )
                }
                }
            </PlaylistContext.Consumer >

        )
    }
}
