import React, { Component } from 'react'

export default class genreChecks extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: props.id,
            name: props.name,
            checked: false
        }
        this.myRef = React.createRef();
    }

    // toggleClick = (e) => {
