    //     this.setState({
    //         id: this.state.id,
    //         name: this.state.name,
    //         checked: !this.state.checked
    //     })
    // }
    render() {
        return (
            <label key={this.state.id} className="genre-option" tabIndex={this.state.id} name={this.state.name}>
                <input type="radio" value={this.state.id} onClick={e => this.props.clickE(e)} name="gen-option" />
                <span className="option-text">{this.state.name}</span>
            </label>
        )

    }
}
