import React, { Component } from 'react'

export default class genreChecks extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: props.id,
            name: props.name,
            // checked: false
        }
        // this.myRef = React.createRef();
    }

    toggleClick = (e) => {
        this.props.clickE(e, this.state.name)
    }
    render() {
        return (
            <div className="genre-option">
                <label key={this.state.id} name={this.state.name} className="genre-option option-text" htmlFor={this.state.id} checked={this.state.selectedGen === this.state.name}>
                    {this.state.name}
                </label>
                <input type="radio" value={this.state.name} name="gen-option" id={this.state.id} />

            </div>
        )

    }
}
