import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PlaylistContext from '../PlaylistContext'
import config from '../config'
// import './Landing.css'


export default class PlaylistDisplay extends Component {

    constructor(props) {
        super(props)

        this.state = {
            playlist: null,
            songs: []
        }

    }

    setPlaylist = (playlist) => {
        this.setState({ playlist })
    }

    addSongs = (song) => {
        this.setState({
            song: [...this.state.songs, song]
        })
    }

    componentDidMount() {
        let playlistId = this.props.match.params.playlistId
        fetch(config.ENDPOINT + `/playlists/${playlistId}`,
            {
                method: 'GET',
                headers: {
                    'content-type': 'application/json'
                }
            })
            .then(res => {
                if (!res.ok)
                    return res.json().then(e => Promise.reject(e))
                return res.json()
            })
            .then(playlist => {
                console.log(playlist)
                this.setPlaylist(playlist)

                fetch(config.ENDPOINT + `/genres/${playlist.genre_id}`,
                    {
                        method: 'GET',
                        headers: {
                            'content-type': 'application/json'
                        }
                    })
                    .then(res2 => {
                        if (!res2.ok)
                            return res2.json().then(e => Promise.reject(e))
                        return res2.json()
                    })
                    .then(genre => {

                        console.log(genre)

                        let runtime = 0
                        let rand = Math.floor(Math.random() * 30);
                        console.log(rand)
                        let queryString = 'genre:%20' + genre.name + '&type=track&limit=50&offset=' + rand

                        let fetData = {
                            qString: queryString,
                        }

                        fetch(`${config.ENDPOINT}/data`, {
                            method: 'POST',
                            headers: {
                                'content-type': 'application/json'
                            },
                            body: JSON.stringify(fetData),
                        })
                            .then(res3 => {
                                if (!res3.ok)
                                    return res3.json().then(e => Promise.reject(e))
                                return res3.json()
                            }).then(response => {
                                console.log(response)
                                let chosen = []
                                let i = 0
                                while (runtime < playlist.length) {
                                    // for (; i < 5; i++) {

                                    let rand = Math.floor(Math.random() * 50);
                                    if (chosen.includes(rand))
                                        i--
                                    else {
                                        chosen.push(rand)
                                        let song = {
                                            url: response.tracks.items[rand].external_urls.spotify,
                                            title: response.tracks.items[rand].name,
                                            length: response.tracks.items[rand].duration_ms,
                                            explicit: response.tracks.items[rand].explicit
                                        }
                                        runtime += song.length
                                        this.addSongs(song)

                                    }
                                    i++

                                    // }
                                }

                            }).catch(error3 => {
                                console.error({ error3 })
                            })




                    }).catch(error2 => {
                        console.error({ error2 });
                    });

            })
            .catch(error => {
                console.error({ error });
            });
        // else
        //     this.mountCount()
        // setTimeout(this.fetcher(), 1000);
        // console.log("running")
    }

    // fetcher() {
    //     console.log(this.props.match.params)
    //     let playlist = this.contextValue.playlists.find(lists => {
    //         return lists.id === this.props.match.params.playlistId
    //     })
