    //     // return <div>{playlist}</div>
    //     // return playlist
    //     // fetch(config.ENDPOINT + '/data', {
    //     //     method: 'POST',
    //     //     headers: {
    //     //         'content-type': 'application/json'
    //     //     },
    //     //     body: JSON.stringify(tinsel),
    //     // })
    //     //     .then(res => {

    //     //         if (!res.ok)
    //     //             return res.json().then(e => Promise.reject(e))
    //     //         return res.json()
    //     //     }).then(res => {
    //     //         console.log(res)
    //     //         // this.props.history.push(`/playlist-display/${created.id}`)
    //     //     }).catch(error => {
    //     //         console.error({ error })
    //     //     })
    //     // }
    //     // }

    //     // </PlaylistContext.Consumer>

    // }
    render() {
        if (this.state.playlist === null) return null;
        return (
            <PlaylistContext.Consumer>

                {(value) => {
                    this.contextValue = value
                    console.log(value)
                    // const output = value.notes.find(note =>
                    //     note.id === parseInt(this.props.match.params.noteId)
                    // )

                    // const folder = value.folders.find(folder =>
                    //     folder.id === output.folder_id
                    // )

                    // const playlistName = this.state.playlist.name

                    return (
                        <div id="noteDisplay">
                            <h2>{this.state.playlist.title}</h2>
                            {/* <article className="displayed"> */}

                            {/* <NoteError> */}
                            {/* <Note id={output.id} name={output.name} content={output.content} mod={output.modified} key={output.id} history={this.props.history} /> */}
                            <p></p>
                            {/* </NoteError> */}
                            {/* </article> */}
                        </div>
                    )
                }
                }
            </PlaylistContext.Consumer >
        )

    }
}
