import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';
import config from './config';
require('dotenv').config();

class App extends Component {
  state = {
    genres: [],
    error: null,
  }

  setGenres = genres => {
    this.setState({
      genres,
      error: null,
    })
  }

  componentDidMount() {
    let endpoint = config.ENDPOINT
    Promise.all([
      fetch(endpoint),
      fetch(endpoint + '/genres')
    ],
      {
        method: 'GET',
        headers: {
          'content-type': 'application/json',
        },
      })
      .then(([apiKeyRes, genreRes]) => {
        if (!apiKeyRes.ok)
          return apiKeyRes.json().then(error => Promise.reject(error))
        if (!genreRes.ok)
          return genreRes.json().then(error => Promise.reject(error))
        return Promise.all([apiKeyRes.json(), genreRes.json()])
      })
      .then(([apiKey, genres]) => {
        this.setGenres(genres)
      })
      .catch(error => {
        console.error({ error });
        this.setState({ error })
      });
  }

  render() {
    const contextValue = {
      genres: this.state.genres
    }



    return (
      <div className="App" >
        <header className="App-header">
          <Link to={'/'}>
            <h1>Playlist-maker</h1>
          </Link>
        </header>
      </div>
    );
  }
}

export default App;
