import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PlaylistContext from '../PlaylistContext'
import './Homepage.css'
import GenreCheck from './GenreCheck'


export default class HomePage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            selected: [],
            hour: 1,
            min: 0,
        }
    }

    clickStore(e, status) {
        if (!status)
            this.setState({ selected: [...this.state.selected, e.target.value] })
        else {
            let temp = this.state.selected.filter(item => {
                if (item !== e.target.value)
                    return item
            })
            this.setState({ selected: temp })
        }

    }

    minChange = (e) => {
        if (e.target.value.length === 1)
            e.target.value = '0' + e.target.value;
        this.setState({
            min: parseInt(e.target.value)
        })
    }

    hourChange = (e) => {
        this.setState({
            hour: parseInt(e.target.value)
        })
    }

    minArr = () => {
        let arr = new Array(60);
        for (let i = 0; i <= 59; i++)
            arr[i] = i

        return arr
    }

    hourArr = () => {
        let arr = new Array(4);
        for (let i = 0; i <= 2; i++)
            arr[i] = i
        return arr
    }

    subHandle = (e) => {
        e.preventDefault();


    }

    render() {

        return (
            <PlaylistContext.Consumer>
                {(value) => {

                    let genres = value.genres.map(genre =>
                        <GenreCheck id={genre.id} name={genre.name} key={genre.id} clickE={this.clickStore} />
                    )

                    let minutes = this.minArr().map(min => {
                        if (min < 10)
                            return <option value={min} key={min}>0{min}</option>
                        else
                            return <option value={min} key={min}>{min}</option>
                    })

                    let hours = this.hourArr().map(min => {
                        return <option value={min} key={min}>{min}</option>
                    })


                    return genres && (
                        <div id="play-stage">
                            <form>
                                <div id="input-wrap">
                                    <div className="inputs">
                                        <label htmlFor="name-input">Playlist Name: </label>
                                        <input type="text" id="name-input" />
                                    </div>
                                    <div className="inputs">
                                        <label htmlFor="hour-length">Playlist length: </label>
                                        <select onChange={this.hourChange}>
                                            <option value={null}>--Hour(s)--</option>
                                            {hours}
                                        </select>
                                    :
                                    <select onChange={this.minChange}>
                                            <option value={null}>--Minute(s)--</option>
                                            {minutes}
                                        </select>
                                    </div>
                                </div>
                                <div id="genres">
                                    {genres}
                                </div>
                                <button type="submit" id="create" onClick={this.subHandle}>Create your playlist!</button>
                            </form>
                        </div>
                    )
                }
                }
            </PlaylistContext.Consumer >

        )
    }
}
