import React, { Component } from 'react'

export default class genreChecks extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: props.id,
            name: props.name,
            checked: false
        }
        this.myRef = React.createRef();
    }

    toggleClick = (e) => {
        this.props.clickE(e, this.state.name)
        this.setState({
            id: this.state.id,
            name: this.state.name,
            checked: !this.state.checked
        })
    }
    render() {
        return (
            <label key={this.state.id} className="genre-option" name={this.state.name} onChange={console.log(this.state.name)}>
                <input type="radio" value={this.state.id} onClick={e => this.props.clickE(e)} name="gen-option" />
                <span className="option-text">{this.state.name}</span>
            </label>
        )

    }
}
