import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PlaylistContext from '../PlaylistContext'
import './Homepage.css'
import GenreCheck from './GenreCheck'


export default class HomePage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            selected: [],
            hour: 1,
            min: 0,
        }
    }

    clickStore = (e, status) => {
        if (!status)
            this.setState({ selected: [...this.state.selected, e.target.value] })
        else {
            let temp = this.state.selected.filter(item => {
                if (item !== e.target.value)
                    return item
            })
            this.setState({ selected: temp })
        }

    }

    minChange = (e) => {
        if (e.target.value.length === 1)
            e.target.value = '0' + e.target.value;
        this.setState({
            min: parseInt(e.target.value)
        })
    }

    hourChange = (e) => {
        this.setState({
            hour: parseInt(e.target.value)
        })
    }

    render() {

        return (
            <PlaylistContext.Consumer>
                {(value) => {
                    let genres = value.genres.map(genre =>
                        <GenreCheck id={genre.id} name={genre.name} key={genre.id} clickE={this.clickStore} />
                    )

                    let minOption = () => {
                        let output
                        for (let i = 0; i <= 59; i++)
                            output += <option value={i}>{i}</option>
                    }

                    return genres && (
                        <div id="play-stage">
                            <div id="input-wrap">
                                <div className="inputs">
                                    <label htmlFor="name-input">Playlist Name: </label>
                                    <input type="text" id="name-input" />
                                </div>
                                <div className="inputs">
                                    <label htmlFor="hour-length">Playlist length:</label>
                                    <input type="number" id="hour-set" min="00" max="3" placeholder="hour" />:<input type="number" id="min-set" min="0" max="59" onChange={this.minChange} placeholder="min" placeholder="minute" />
                                </div>
                            </div>
                            <div id="genres">
                                {genres}
                            </div>
                            <footer><Link to={'/'}>Existing Playlists</Link></footer>
                        </div>
                    )
                }
                }
            </PlaylistContext.Consumer >

        )
    }
}
