import React, { Component } from 'react'

export default class genreChecks extends Component {
    constructor(props) {
        super(props);
        console.log(props)

        this.state = {
            id: props.id,
            name: props.name,
            checked: this.checkedState()
        }
    }

    checkedState = () => {
        if (this.state != undefined)
            return this.state.checked
        else
            return false
    }

    toggleClick = () => {
        if (this.state.checked)
            this.setState({
                id: this.state.id,
                name: this.state.name,
                checked: false
            })
        else
            this.setState({
                id: this.state.id,
                name: this.state.name,
                checked: false
            })
        console.log(this.state.checked)
        // this.componentDidMount()
    }
    render() {
        if (this.state.checked == true)
            return (
                <label key={this.state.id} className="genre-option checked" onClick={this.toggleClick} >
                    <input type="checkbox" value={this.state.name} />
                    <span className="option-text">{this.state.name}</span>
                </label>
            )
        else
            return (
                <label key={this.state.id} className="genre-option" onClick={this.toggleClick} >
                    <input type="checkbox" value={this.state.name} />
                    <span className="option-text">{this.state.name}</span>
                </label>)
    }
}
