import React, { Component } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import './App.css';
import config from './config';
import PlaylistContext from './PlaylistContext'
import Landing from './Landingpage/Landing'
import Homepage from './Homepage/Homepage'

class App extends Component {

  state = {
    genres: [],
    playlists: [],
    records: [],
    user: null,
    error: null,
  }

  setGenres = genres => {
    this.setState({
      genres
    })
  }

  setPlaylists = playlists => {
    this.setState({
      playlists
    })
  }

  setRecords = records => {
    this.setState({
      records
    })
  }

  componentDidMount() {
    let endpoint = config.ENDPOINT
    Promise.all([
      fetch(endpoint + '/playlists'),
      fetch(endpoint + '/genres'),
      fetch(endpoint + '/playlistRecords')
    ],
      {
        method: 'GET',
        headers: {
          'content-type': 'application/json',
        },
      })
      .then(([playlistRes, genreRes, recordRes]) => {
        if (!playlistRes.ok)
          return playlistRes.json().then(error => Promise.reject(error))
        if (!genreRes.ok)
          return genreRes.json().then(error => Promise.reject(error))
        if (!recordRes.ok)
          return recordRes.json().then(error => Promise.reject(error))
        return Promise.all([playlistRes.json(), genreRes.json(), recordRes.json()])
      })
      .then(([playlist, genres, records]) => {
        this.setPlaylists(playlist)
        this.setGenres(genres)
        this.setRecords(records)
      })
      .catch(error => {
        console.error({ error });
        this.setState({ error })
      });
  }

  pageUpdate = () => {
    this.componentDidMount()
  }

  render() {
    const contextValue = {
      genres: this.state.genres,
      playlists: this.state.playlists,
      pageUpdate: this.pageUpdate
    }

    return (
      <div className="App" >
        <header className="App-header">
          <Link to={'/'} id="home-link">
            <h1>ShowTunes</h1>
          </Link>
          <hr />
        </header>
        <PlaylistContext.Provider value={contextValue}>
          <main id="stage">
            <Switch>
              <Route exact path='/' component={Landing} />
              <Route path='/homepage' component={Homepage} />
              <Route path='/existing-playlists' component={Homepage} />
              <Route path='/playlist-display/:playlist-id' component={Homepage} />
            </Switch>

          </main>
        </PlaylistContext.Provider>
      </div>
    );
  }
}

export default App;
