import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';
import config from './config';

class App extends Component {
  state = {
    genres: [],
    apiSuccess: false,
    error: null,
  }

  setGenres = genres => {
    this.setState({
      genres,
      apiSuccess: this.state.apiSuccess,
      error: null,
    })
  }

  setApiSuccess = success => {
    this.setState({
      genres,
      apiSuccess: this.state.apiSuccess,
      error: null,
    })
  }

  componentDidMount() {
    let endpoint = config.ENDPOINT
    Promise.all([
      fetch(endpoint),
      fetch(endpoint + '/genres')
    ],
      {
        method: 'GET',
        headers: {
          'content-type': 'application/json',
        },
      })
      .then(([apiKeyRes, genreRes]) => {
        if (!apiKeyRes.ok)
          return apiKeyRes.json().then(error => Promise.reject(error))
        if (!genreRes.ok)
          return genreRes.json().then(error => Promise.reject(error))
        return Promise.all([apiKeyRes.json(), genreRes.json()])
      })
      .then((key, genres) => {
        this.setGenres(genres)
        process.env['API_KEY'] = key
        console.log(this.state.genres)
        console.log(process.env.API_KEY)
      })
      .catch(error => {
        console.error({ error });
        this.setState({ error })
      });
  }

  render() {
    return (
      <div className="App" >
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
        </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
        </a>
        </header>
      </div>
    );
  }
}

export default App;
