import { Link } from 'react-router-dom'
import PlaylistContext from '../PlaylistContext'
import config from '../config'
// import './Landing.css'


export default class PlaylistDisplay extends Component {

    constructor(props) {
        super(props)

        this.state = {
            playlist: null
        }

    }

    // mountCount = () => {
