        this.setState({ playlist })
    }

    componentDidMount() {
        let playlistId = this.props.match.params.playlistId
        // if (this.state.thirdMount === 3)
        fetch(config.ENDPOINT + `/playlists/${playlistId}`,
            {
                method: 'GET',
                headers: {
                    'content-type': 'application/json'
                }
            })
            .then(res => {
                if (!res.ok)
                    return res.json().then(e => Promise.reject(e))
                return res.json()
            })
            .then(playlist => {
                // this.setFetchTrue()
                console.log(playlist)
                this.setPlaylist(playlist)

                fetch(config.ENDPOINT + `/playlistRecords/${playlistId}`,
                    {
                        method: 'GET',
                        headers: {
                            'content-type': 'application/json'
                        }
                    })
                    .then(res2 => {
                        if (!res2.ok)
                            return res2.json().then(e => Promise.reject(e))
                        return res2.json()
                    })
                    .then(records => {

                        console.log(records)

                        let genres = []
                        for (let i = 0; i < records.length; i++) {
                            let output = this.props.location.state.find(record => {
                                return records[i].genre_id === record.id
                            })
                            genres.push(output)

                        }

                        console.log(genres)




